local NAME, S = ...local KCL = KethoCombatLoglocal ACR = LibStub("AceConfigRegistry-3.0")local ACD = LibStub("AceConfigDialog-3.0")--local LB_Race = LibStub("LibBabble-Race-3.0"):GetBaseLookupTable()--local LB_TalentTree = LibStub("LibBabble-TalentTree-3.0"):GetBaseLookupTable()local L = S.Llocal player = S.Playerlocal profile, charlocal color, spellfunction KCL:RefreshDB2()	profile, char = self.db.profile, self.db.char	color, spell = profile.color, profile.spellendlocal _G = _Glocal unpack = unpacklocal format = format--[=[	------------------------	--- Scanning Tooltip ---	------------------------local tooltip = CreateFrame("GameTooltip", "KethoCombatLogScanTooltip", UIParent, "GameTooltipTemplate")tooltip:SetOwner(UIParent, "ANCHOR_NONE")local scanCache, dumpCache = {}, {}scanCache.spell = {}local function ScanTooltip(linkType, id, line)	if scanCache[linkType][id] then		return scanCache[linkType][id]	else		-- problem: sometimes the tooltip dies, and not sure how to "reactivate it"		-- trying to avoid setting the same spell again; this should also be prevented by using caching		if select(3, tooltip:GetSpell()) ~= id then			tooltip:SetHyperlink(linkType..":"..id)		end		wipe(dumpCache)		-- this is a bit ugly with the coloring and all :s		for i = 2, line do			if i == line then				-- problem: a line can be omitted depending on the current glyphs+class				tinsert(dumpCache, "|r"..(_G["KethoCombatLogScanTooltipTextLeft"..i]:GetText() or "|cffFF0000Error|r"))				break			end			tinsert(dumpCache, _G["KethoCombatLogScanTooltipTextLeft"..i]:GetText())			tinsert(dumpCache, _G["KethoCombatLogScanTooltipTextRight"..i]:GetText())		end		scanCache[linkType][id] = "|cffFFFF00"..strjoin("\n", unpack(dumpCache))		return scanCache[linkType][id]	endend	--------------------------	--- Extra Tooltip Info ---	---------------------------- determine if it's a mana class-- tooltips may vary depending on if the player's class utilises manalocal ManaClass = {	PALADIN = true,	SHAMAN = true,	DRUID = true,	PRIEST = true,	MAGE = true,	WARLOCK = true,}local isManaClass = ManaClass[player.class]-- could have used data from EncounterJournal_SetFlagIcon,-- but there it included some whitespace, so using own coords nowlocal EJ_Coords = {	{2, 6, 32, 96}, -- tank	{10, 14, 32, 96}, -- dps	{18, 22, 32, 96}, -- healer	{26, 30, 32, 96}, -- heroic	{34, 38, 32, 96}, -- deadly	{42, 46, 32, 96}, -- imporant	{50, 54, 32, 96}, -- interruptible	{58, 62, 32, 96}, -- magic	{2, 6, 120, 224}, -- curse	{10, 14, 160, 224}, -- poison	{18, 22, 160, 224}, -- disease	{26, 30, 160, 224}, -- enrage}-- show buff typelocal function EJ_Desc(index)	local iconCoords = strjoin(":", unpack(EJ_Coords[index+1]))	local iconString = "|TInterface\\EncounterJournal\\UI-EJ-Icons:18:18:1:"..(index == 11 and 1 or 3)..":64:256:"..iconCoords.."|t"	return format("%s %s\n", iconString, _G["ENCOUNTER_JOURNAL_SECTION_FLAG"..index])endlocal schoolDescInfo = {	HOLY = {2, 0x2, "FFE680"},	FIRE = {3, 0x4, "FF8000"},	NATURE = {4, 0x8, "4DFF4D"},	FROST = {5, 0x10, "80FFFF"},	SHADOW = {6, 0x20, "8080FF"},	ARCANE = {7, 0x40, "FF80FF"},}-- show school typelocal function SchoolDesc(school)	local values = schoolDescInfo[school]		local iconString = "|TInterface\\PaperDollInfoFrame\\SpellSchoolIcon"..values[1]..":18:18:1:3:16:16:1:15:1:15|t"	local nameString = S.SpellSchoolString[values[2]]	return iconString.." |cff"..values[3]..nameString.."|r\n"end-- talent descriptionlocal function TalentDesc(amount, spec)	return format("|cffADFF2F%d %s %s|r\n", amount, LB_TalentTree[spec], TALENTS)end]=]	----------------	--- Defaults ---	----------------S.defaults = {	profile = { -- KethoCombatLog.db.defaults.profile		LocalTaunt = true,		LocalInterrupt = true,		LocalDeath = true,				PvE = true,		PvP = true,		World = true,				ChatWindow = 2, -- ChatFrame1		ChatChannel = 1, -- Disabled				TrimRealmName = true,		Timestamp = 1, -- None		IconSize = 16,		IconCropped = true,		UnitBracesChat = true,		CriticalFormat = true,		BlizzardCombatLog = true,				FilterSelf = true,		FilterOther = true,		FilterPlayers = true,		TankTaunt = true,		FriendlyDispel = true,		HostileDispel = true,		Spellsteal = true,		MissTaunt = true,		MissInterrupt = true,		MissCC = true,				color = {			Taunt = {1, 0, 0}, -- #FF0000 (Red)			Interrupt = {0, 110/255, 1}, -- #006EFF (something Blue)			Juke = {1, 1, 1},			Dispel = {1, 1, 1},			Reflect = {1, 1, 1},			CrowdControl = {1, 1, 1},			Break = {1, 1, 1},			Death = {1, 1, 1},			Save = {1, 1, 1},			Resurrect = {175/255, 1, 47/255}, -- #ADFF2F (GreenYellow)			-- Constants.lua: SCHOOL_MASK_PHYSICAL, ...			Physical = {1.00, 1.00, 0.00}, -- #FFFF00			Holy = {1.00, 0.90, 0.50}, -- #FFE680			Fire = {1.00, 0.50, 0.00}, -- #FF8000			Nature = {0.30, 1.00, 0.30}, -- #4DFF4D			Frost = {0.50, 1.00, 1.00}, -- #80FFFF			Shadow = {0.50, 0.50, 1.00}, -- #8080FF			Arcane = {1.00, 0.50, 1.00}, -- #FF80FF			-- /dump COMBATLOG_DEFAULT_COLORS.unitColoring[COMBATLOG_FILTER_FRIENDLY_UNITS]			Friendly = {0.34, 0.64, 1.00}, -- #57A3FF			Hostile = {0.75, 0.05, 0.05}, -- #BF0D0D			Unknown = {191/255, 191/255, 191/255}, -- #BFBFBF			Timestamp = {0.67, 0.67, 0.67},		},				message = {			Taunt = "<SRC><SPELL><ICON> taunted <DEST>",			Taunt_AoE = "<SRC><SPELL><ICON> AoE "..S.EventString.Taunt[1],			Growl = "<SRC><SPELL><ICON> growled <DEST>",						Interrupt = "<SRC><SPELL><ICON> "..ACTION_SPELL_INTERRUPT.." <DEST><XSPELL><XICON>",			Juke = "<SRC> juked <SPELL><ICON> on <DEST>",						-- http://us.battle.net/wow/en/forum/topic/6413023639			-- "The Combat log is reporting purges, friendly dispels, and spellsteals backwards."			Dispel = "<SRC><XSPELL><XICON> "..ACTION_SPELL_DISPEL_BUFF.." <DEST><SPELL><ICON>",			Cleanse = "<SRC><XSPELL><XICON> "..ACTION_SPELL_DISPEL_DEBUFF.." <DEST><SPELL><ICON>",			Spellsteal = "<SRC><XSPELL><XICON> "..ACTION_SPELL_STOLEN.." <DEST><SPELL><ICON>",						Reflect = "<DEST> "..ACTION_SPELL_MISSED_REFLECT.." <SRC><SPELL><ICON>",			Miss = "<SRC><SPELL><ICON> on <DEST> "..ACTION_SPELL_CAST_FAILED.." (<TYPE>)",						CrowdControl = "<SRC><SPELL><ICON> CC'ed <DEST>",			Break = "<SRC> "..ACTION_SPELL_AURA_BROKEN.." <SPELL><ICON> on <DEST>",			Break_NoSource = "<SPELL><ICON> on <DEST> "..ACTION_SPELL_AURA_BROKEN,			Break_Spell = "<SRC><SPELL><ICON> "..ACTION_SPELL_AURA_BROKEN.." <XSPELL><XICON> on <DEST>",						Death = "<DEST> "..ACTION_UNIT_DIED.." <SRC><SPELL><ICON> <AMOUNT> <SCHOOL>",			Death_Melee = "<DEST> "..ACTION_UNIT_DIED.." <SRC> <AMOUNT> "..ACTION_SWING,			Death_Environmental = "<DEST> "..ACTION_UNIT_DIED.." <AMOUNT> <TYPE>",			Death_Instakill = "<SRC><SPELL><ICON> "..ACTION_SPELL_INSTAKILL.." <DEST>",						Save = "<SRC><SPELL><ICON> saved <DEST> <AMOUNT> <SCHOOL>",			Resurrect = "<SRC><SPELL><ICON> "..ACTION_SPELL_RESURRECT.." <DEST>",						--Spell = "<SPELL><ICON<ICON>",		},				sink20OutputSink = "None",	},}local defaults = S.defaultsfor k, v in pairs(RAID_CLASS_COLORS) do	-- this seems to be safe, ie seperate table references	defaults.profile.color[k] = vend	---------------	--- Options ---	---------------S.options = {	type = "group",	childGroups = "tab",	name = format("%s |cffADFF2Fv%s|r", "Ketho |cffFFFFFFCombatLog|r", S.VERSION),	args = {		Main = {			type = "group", order = 1,			name = "|TInterface\\Icons\\INV_Sword_01:16:16:0:-1"..S.crop.."|t  Main",			handler = KCL,			get = "GetValue", set = "SetValue",			disabled = "OptionsDisabled",			args = {				Local = {					type = "group", order = 1,					inline = true,					name = function() return profile.ChatFilter and "|cff71D5FFLocal|r" or " " end,					args = {}, -- populated later				},				Chat = {					type = "group", order = 2,					inline = true,					name = function() return profile.ChatFilter and "|cff71D5FF"..CHAT.."|r" or " " end,					hidden = function() return not profile.ChatFilter end,					args = {}, -- populated later				},				spacing = {type = "description", order = 3, name = ""},				PvE = {					type = "toggle", order = 4,					width = "half", descStyle = "",					name = " |cffA8A8FFPvE|r", -- /dump ChatTypeInfo.PARTY {r = 0.666, g = 0.666, b = 1.000}				},				PvP = {					type = "toggle", order = 5,					width = "half", descStyle = "",					name = " |cffFF7F00"..PVP.."|r", -- /dump ChatTypeInfo.RAID {r = 1.000, g = 0.498, b = 0.000}				},				World = {					type = "toggle", order = 6,					width = "half", descStyle = "",					name = " World",				},				ChatFilter = {					type = "toggle", order = 7,					descStyle = "",					name = "|cff71D5FF"..CHAT.." "..FILTERS.."|r",				},				newline1 = {type = "description", order = 8, name = ""},				ChatWindow = {					type = "select", order = 9,					descStyle = "",					name = "   |cffFFFFFF"..CHAT.." Window|r",					values = function()						local ChatWindowList = {"|cffFF0000<"..ADDON_DISABLED..">|r"}						for i = 1, NUM_CHAT_WINDOWS do							local window = GetChatWindowInfo(i)							if window ~= "" then								ChatWindowList[i+1] = "|cff2E9AFE"..i..".|r  "..window							end						end						return ChatWindowList end,					set = function(i, v) profile.ChatWindow = v						if v > 1 then							S.ChatFrame = _G["ChatFrame"..v-1]							S.ChatFrame:AddMessage("|cffADFF2FKetho CombatLog|r: Chat Frame |cff57A3FF"..(v-1)..".|r |cffADFF2F"..GetChatWindowInfo(v-1).."|r")						end					end,				},				ChatChannel = {					type = "select", order = 10,					descStyle = "",					name = "   |cffFFFFFF"..CHAT.." "..CHANNEL.."|r",					values = function()						local ChatChannelList = {							"|cffFF0000<"..ADDON_DISABLED..">|r",							"|cff2E9AFE#|r   "..CHAT_MSG_SAY,							"|cff2E9AFE#|r   |cffFF4040"..CHAT_MSG_YELL.."|r",							"|cff2E9AFE#|r   |cffA8A8FF"..CHAT_MSG_PARTY.."|r / |cffFF7F00"..CHAT_MSG_RAID.."|r",						}						for i = 1, 10 do							l